span> <span class="p">{</span></div><div class='line' id='LC919'>		<span class="nb">push</span><span class="p">(</span><span class="nv">@generalrec</span><span class="p">,</span><span class="s">&quot;Enable concurrent_insert by setting it to 1&quot;</span><span class="p">);</span></div><div class='line' id='LC920'>	<span class="p">}</span></div><div class='line' id='LC921'>	<span class="k">if</span> <span class="p">(</span><span class="nv">$mycalc</span><span class="p">{</span><span class="s">&#39;pct_aborted_connections&#39;</span><span class="p">}</span> <span class="o">&gt;</span> <span class="mi">5</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC922'>		<span class="n">badprint</span> <span class="s">&quot;Connections aborted: &quot;</span><span class="o">.</span><span class="nv">$mycalc</span><span class="p">{</span><span class="s">&#39;pct_aborted_connections&#39;</span><span class="p">}</span><span class="o">.</span><span class="s">&quot;%\n&quot;</span><span class="p">;</span></div><div class='line' id='LC923'>		<span class="nb">push</span><span class="p">(</span><span class="nv">@generalrec</span><span class="p">,</span><span class="s">&quot;Your applications are not closing MySQL connections properly&quot;</span><span class="p">);</span></div><div class='line' id='LC924'>	<span class="p">}</span></div><div class='line' id='LC925'><br/></div><div class='line' id='LC926'>	<span class="c1"># InnoDB</span></div><div class='line' id='LC927'>	<span class="k">if</span> <span class="p">(</span><span class="nb">defined</span> <span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;have_innodb&#39;</span><span class="p">}</span> <span class="o">&amp;&amp;</span> <span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;have_innodb&#39;</span><span class="p">}</span> <span class="ow">eq</span> <span class="s">&quot;YES&quot;</span> <span class="o">&amp;&amp;</span> <span class="nb">defined</span> <span class="nv">$enginestats</span><span class="p">{</span><span class="s">&#39;InnoDB&#39;</span><span class="p">})</span> <span class="p">{</span></div><div class='line' id='LC928'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;innodb_buffer_pool_size&#39;</span><span class="p">}</span> <span class="o">&gt;</span> <span class="nv">$enginestats</span><span class="p">{</span><span class="s">&#39;InnoDB&#39;</span><span class="p">})</span> <span class="p">{</span></div><div class='line' id='LC929'>			<span class="n">goodprint</span> <span class="s">&quot;InnoDB data size / buffer pool: &quot;</span><span class="o">.</span><span class="n">hr_bytes</span><span class="p">(</span><span class="nv">$enginestats</span><span class="p">{</span><span class="s">&#39;InnoDB&#39;</span><span class="p">})</span><span class="o">.</span><span class="s">&quot;/&quot;</span><span class="o">.</span><span class="n">hr_bytes</span><span class="p">(</span><span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;innodb_buffer_pool_size&#39;</span><span class="p">})</span><span class="o">.</span><span class="s">&quot;\n&quot;</span><span class="p">;</span></div><div class='line' id='LC930'>		<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC931'>			<span class="n">badprint</span> <span class="s">&quot;InnoDB data size / buffer pool: &quot;</span><span class="o">.</span><span class="n">hr_bytes</span><span class="p">(</span><span class="nv">$enginestats</span><span class="p">{</span><span class="s">&#39;InnoDB&#39;</span><span class="p">})</span><span class="o">.</span><span class="s">&quot;/&quot;</span><span class="o">.</span><span class="n">hr_bytes</span><span class="p">(</span><span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;innodb_buffer_pool_size&#39;</span><span class="p">})</span><span class="o">.</span><span class="s">&quot;\n&quot;</span><span class="p">;</span></div><div class='line' id='LC932'>			<span class="nb">push</span><span class="p">(</span><span class="nv">@adjvars</span><span class="p">,</span><span class="s">&quot;innodb_buffer_pool_size (&gt;= &quot;</span><span class="o">.</span><span class="n">hr_bytes_rnd</span><span class="p">(</span><span class="nv">$enginestats</span><span class="p">{</span><span class="s">&#39;InnoDB&#39;</span><span class="p">})</span><span class="o">.</span><span class="s">&quot;)&quot;</span><span class="p">);</span></div><div class='line' id='LC933'>		<span class="p">}</span></div><div class='line' id='LC934'>	<span class="p">}</span></div><div class='line' id='LC935'><span class="p">}</span></div><div class='line' id='LC936'><br/></div><div class='line' id='LC937'><span class="c1"># Take the two recommendation arrays and display them at the end of the output</span></div><div class='line' id='LC938'><span class="k">sub </span><span class="nf">make_recommendations</span> <span class="p">{</span></div><div class='line' id='LC939'>	<span class="k">print</span> <span class="s">&quot;\n-------- Recommendations -----------------------------------------------------\n&quot;</span><span class="p">;</span></div><div class='line' id='LC940'>	<span class="k">if</span> <span class="p">(</span><span class="nv">@generalrec</span> <span class="o">&gt;</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC941'>		<span class="k">print</span> <span class="s">&quot;General recommendations:\n&quot;</span><span class="p">;</span></div><div class='line' id='LC942'>		<span class="k">foreach</span> <span class="p">(</span><span class="nv">@generalrec</span><span class="p">)</span> <span class="p">{</span> <span class="k">print</span> <span class="s">&quot;    &quot;</span><span class="o">.</span><span class="nv">$_</span><span class="o">.</span><span class="s">&quot;\n&quot;</span><span class="p">;</span> <span class="p">}</span></div><div class='line' id='LC943'>	<span class="p">}</span></div><div class='line' id='LC944'>	<span class="k">if</span> <span class="p">(</span><span class="nv">@adjvars</span> <span class="o">&gt;</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC945'>		<span class="k">print</span> <span class="s">&quot;Variables to adjust:\n&quot;</span><span class="p">;</span></div><div class='line' id='LC946'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$mycalc</span><span class="p">{</span><span class="s">&#39;pct_physical_memory&#39;</span><span class="p">}</span> <span class="o">&gt;</span> <span class="mi">90</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC947'>			<span class="k">print</span> <span class="s">&quot;  *** MySQL&#39;s maximum memory usage is dangerously high ***\n&quot;</span><span class="o">.</span></div><div class='line' id='LC948'>				  <span class="s">&quot;  *** Add RAM before increasing MySQL buffer variables ***\n&quot;</span><span class="p">;</span></div><div class='line' id='LC949'>		<span class="p">}</span></div><div class='line' id='LC950'>		<span class="k">foreach</span> <span class="p">(</span><span class="nv">@adjvars</span><span class="p">)</span> <span class="p">{</span> <span class="k">print</span> <span class="s">&quot;    &quot;</span><span class="o">.</span><span class="nv">$_</span><span class="o">.</span><span class="s">&quot;\n&quot;</span><span class="p">;</span> <span class="p">}</span></div><div class='line' id='LC951'>	<span class="p">}</span></div><div class='line' id='LC952'>	<span class="k">if</span> <span class="p">(</span><span class="nv">@generalrec</span> <span class="o">==</span> <span class="mi">0</span> <span class="o">&amp;&amp;</span> <span class="nv">@adjvars</span> <span class="o">==</span><span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC953'>		<span class="k">print</span> <span class="s">&quot;No additional performance recommendations are available.\n&quot;</span></div><div class='line' id='LC954'>	<span class="p">}</span></div><div class='line' id='LC955'>	<span class="k">print</span> <span class="s">&quot;\n&quot;</span><span class="p">;</span></div><div class='line' id='LC956'><span class="p">}</span></div><div class='line' id='LC957'><br/></div><div class='line' id='LC958'><span class="c1"># ---------------------------------------------------------------------------</span></div><div class='line' id='LC959'><span class="c1"># BEGIN &#39;MAIN&#39;</span></div><div class='line' id='LC960'><span class="c1"># ---------------------------------------------------------------------------</span></div><div class='line' id='LC961'><span class="k">print</span>	<span class="s">&quot;\n &gt;&gt;  MySQLTuner $tunerversion - Major Hayden &lt;major\@mhtx.net&gt;\n&quot;</span><span class="o">.</span></div><div class='line' id='LC962'>		<span class="s">&quot; &gt;&gt;  Bug reports, feature requests, and downloads at http://mysqltuner.com/\n&quot;</span><span class="o">.</span></div><div class='line' id='LC963'>		<span class="s">&quot; &gt;&gt;  Run with &#39;--help&#39; for additional options and output filtering\n&quot;</span><span class="p">;</span></div><div class='line' id='LC964'><span class="n">mysql_setup</span><span class="p">;</span>					<span class="c1"># Gotta login first</span></div><div class='line' id='LC965'><span class="n">os_setup</span><span class="p">;</span>						<span class="c1"># Set up some OS variables</span></div><div class='line' id='LC966'><span class="n">get_all_vars</span><span class="p">;</span>					<span class="c1"># Toss variables/status into hashes</span></div><div class='line' id='LC967'><span class="n">validate_tuner_version</span><span class="p">;</span>			<span class="c1"># Check current MySQLTuner version</span></div><div class='line' id='LC968'><span class="n">validate_mysql_version</span><span class="p">;</span>			<span class="c1"># Check current MySQL version</span></div><div class='line' id='LC969'><span class="n">check_architecture</span><span class="p">;</span>				<span class="c1"># Suggest 64-bit upgrade</span></div><div class='line' id='LC970'><span class="n">check_storage_engines</span><span class="p">;</span>			<span class="c1"># Show enabled storage engines</span></div><div class='line' id='LC971'><span class="n">security_recommendations</span><span class="p">;</span>		<span class="c1"># Display some security recommendations</span></div><div class='line' id='LC972'><span class="n">calculations</span><span class="p">;</span>					<span class="c1"># Calculate everything we need</span></div><div class='line' id='LC973'><span class="n">mysql_stats</span><span class="p">;</span>					<span class="c1"># Print the server stats</span></div><div class='line' id='LC974'><span class="n">make_recommendations</span><span class="p">;</span>			<span class="c1"># Make recommendations based on stats</span></div><div class='line' id='LC975'><span class="c1"># ---------------------------------------------------------------------------</span></div><div class='line' id='LC976'><span class="c1"># END &#39;MAIN&#39;</span></div><div class='line' id='LC977'><span class="c1"># ---------------------------------------------------------------------------</span></div><div class='line' id='LC978'><br/></div><div class='line' id='LC979'><span class="c1"># Local variables:</span></div><div class='line' id='LC980'><span class="c1"># indent-tabs-mode: t</span></div><div class='line' id='LC981'><span class="c1"># cperl-indent-level: 8</span></div><div class='line' id='LC982'><span class="c1"># perl-indent-level: 8</span></div><div class='line' id='LC983'><span class="c1"># End:</span></div></pre></div>
